css:
.print-format {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  font-size: 12px;
  line-height: 1.4;
}

.header {
  text-align: center;
  margin-bottom: 30px;
  border-bottom: 2px solid #007bff;
  padding-bottom: 15px;
}

.student-info {
  background: #f8f9fa;
  padding: 15px;
  border-radius: 5px;
  margin-bottom: 20px;
}

.grades-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;
}

.grades-table th, .grades-table td {
  border: 1px solid #ddd;
  padding: 8px;
  text-align: center;
}

.grades-table th {
  background-color: #007bff;
  color: white;
}

.footer {
  margin-top: 30px;
  text-align: center;
  font-size: 10px;
  color: #666;
}

.print-format {
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  font-size: 12px;
}

.fee-header {
  text-align: center;
  margin-bottom: 25px;
  border-bottom: 2px solid #28a745;
  padding-bottom: 15px;
}

.fee-details {
  background: #f8f9fa;
  padding: 15px;
  border-radius: 5px;
  margin-bottom: 20px;
}

.fee-table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;
}

.fee-table th, .fee-table td {
  border: 1px solid #ddd;
  padding: 10px;
  text-align: left;
}

.fee-table th {
  background-color: #28a745;
  color: white;
}

.total-row {
  background-color: #e9ecef;
  font-weight: bold;
}

.payment-info {
  margin-top: 20px;
  padding: 15px;
  background: #fff3cd;
  border-left: 4px solid #ffc107;
}
